import React, { useState, useEffect } from "react";

import "./Quiz.css";

const FlashCard = ({ question, answer, incrementIndex }) => {
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => setShowAnswer(false), [question]);

  return (
    <>
      <div
        className="flashcard-container"
        onClick={() => setShowAnswer(!showAnswer)}
      >
        {/* {!showAnswer && question}
        {showAnswer && answer} */}
        <a href="https://www.linkedin.com/in/akash-sirohi-676b30202/">Linkedin</a>
      </div>
      {showAnswer && (
        <button onClick={incrementIndex} className="flashcard-button">
          Oth
        </button>
      )}
    </>
  );
};

export default FlashCard;
