class ActionProvider {
    constructor(createChatBotMessage, setStateFunc, createClientMessage) {
      this.createChatBotMessage = createChatBotMessage;
      this.setState = setStateFunc;
      this.createClientMessage = createClientMessage;
    }

    greet = () => {
        var message = this.createChatBotMessage("Hello Friend")
        this.addMessageToState(message); 
        var message = this.createChatBotMessage("How can I help you ?")
        this.addMessageToState(message); 
    }

    empty =() => {
        var message = this.createChatBotMessage("Pleas")
        this.addMessageToState(message); 
    }
    reet = () => {
        var message = this.createChatBotMessage("Sorry Sir I did not understand that")
        this.addMessageToState(message); 
        var message = this.createChatBotMessage("Please choose any one option 1. Contact on Telegram 2. Mail me")
        this.addMessageToState(message); 
        // var ap = <a href="https://gist.github.com/FredrikOseberg/c1e8ec83ade6e89ca84882e33caf599c">Linkedin</a>
        // this.addMessageToState(ap);
    }
    addMessageToState = (message) => {
        this.setState(prevState => ({
            ...prevState,
            messages : [...prevState.messages, message],
        }));
    }
  }
  
  export default ActionProvider;
